#!/bin/sh
# System Information
hostname | { echo "Name" ; cat ; echo ; } | sh /usr/local/bin/pfMailReport.sh -r 'System Information' && uptime | { echo "Uptime" ; cat ; echo ; } | sh /usr/local/bin/pfMailReport.sh -r && uname -mrs | { echo "OS" ; cat ; echo ; } | sh /usr/local/bin/pfMailReport.sh -r && dmidecode -t 0 | awk '/Vendor/{x=NR+2}(NR<=x){print}' | { echo "BIOS" ; cat ; echo ; } | sh /usr/local/bin/pfMailReport.sh -r && dmesg | grep CPU | { echo "CPU" ; cat ; echo ; } | sh /usr/local/bin/pfMailReport.sh -r && dmesg | grep memory | head -2 | { echo "Memory" ; cat ; echo ; } | sh /usr/local/bin/pfMailReport.sh -r && top | head -5 | { echo "Running" ; cat ; echo ; } | sh /usr/local/bin/pfMailReport.sh -r && who | { echo "Users" ; cat ; echo ; } | sh /usr/local/bin/pfMailReport.sh -r
